mapper.select:
SELECT * FROM t_shop


        AND name LIKE CONCAT('%', #{name}, '%')


        AND category_id = #{categoryId}


        AND shop_type = #{shopType}


        AND status = #{status}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}


ORDER BY

    ${sortField} ${sortOrder},

sort_order ASC, id DESC

    LIMIT #{start}, #{limit}

SELECT COUNT(*) FROM t_shop


        AND name LIKE CONCAT('%', #{name}, '%')


        AND category_id = #{categoryId}


        AND shop_type = #{shopType}


        AND status = #{status}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}
